	, [InDCDate], [CFMShipment], [PFETA], [PackLETA], [LETA], [MRHandle], [SMR], [ScanAndPack], [VasShas], [SpecialCust]
	, [TissuePaper], [Junk], [Packing], [MarkFront], [MarkBack], [MarkLeft], [MarkRight], [Label], [OrderRemark], [ArtWorkCost]
	, [StdCost], [CtnType], [FOCQty], [SMnorderApv], [FOC], [MnorderApv2], [Packing2], [SampleReason], [RainwearTestPassed]
	, [SizeRange], [MTLComplete], [SpecialMark], [OutstandingRemark], [OutstandingInCharge], [OutstandingDate], [OutstandingReason]
	, [StyleUkey], [POID], [OrderComboID], [IsNotRepeatOrMapping], [SplitOrderId], [FtyKPI], [AddName], [AddDate], [EditName], [EditDate]
	, [SewLine], [ActPulloutDate], [ProdSchdRemark], [IsForecast], [LocalOrder], [GMTClose], [TotalCTN], [ClogCTN], [FtyCTN]
	, [PulloutComplete], [ReadyDate], [PulloutCTNQty], [Finished], [PFOrder], [SDPDate], [InspDate], [InspResult], [InspHandle]
	, [KPILETA], [MTLETA], [SewETA], [PackETA], [MTLExport], [DoxType], [FtyGroup], [MDivisionID], [CutReadyDate], [SewRemark]
	, [WhseClose], [SubconInSisterFty], [MCHandle], [LocalMR], [KPIChangeReason], [MDClose], [MDEditName], [MDEditDate]
	, [ClogLastReceiveDate], [CPUFactor], [SizeUnit], [CuttingSP], [IsMixMarker], [EachConsSource], [KPIEachConsApprove]
